drivingTest <- function(age){
  if(age >= 17){
    status = "You can drive!"
  } else if(age >= 16){
    status = "You are almost old enough to drive!"
  } else{
    status = "You are not old enough to drive."
  }
  return(paste0("You are ", age, " years old. ", status))
}
