remove.let <- function(s) {

  s_sep <- str_split(s, pattern = " ")[[1]]

  output <- vector("character", length = length(s_sep))

  for (i in 1:length(s_sep)) {
    output[i] <- str_sub(s_sep[i], 2, -1)
  }

  return(str_c(output, collapse = " "))

}

